#!/usr/bin/env node

const { execSync } = require('child_process');

try {
  const output = execSync('npx eslint scripts/*.js --format json', { encoding: 'utf-8', stdio: ['pipe', 'pipe', 'pipe'] });
  const json = JSON.parse(output);

  const errors = {};
  const errorFiles = {};

  json.forEach(file => {
    file.messages.filter(m => m.severity === 2).forEach(msg => {
      errors[msg.ruleId] = (errors[msg.ruleId] || 0) + 1;
      if (!errorFiles[msg.ruleId]) errorFiles[msg.ruleId] = [];
      errorFiles[msg.ruleId].push({
        file: file.filePath.split('\\').pop(),
        line: msg.line,
        message: msg.message
      });
    });
  });

  console.log('\nüìä ESLint ÏóêÎü¨ ÌÜµÍ≥Ñ\n');
  console.log('='.repeat(60));

  const sorted = Object.entries(errors).sort((a, b) => b[1] - a[1]);

  sorted.forEach(([rule, count]) => {
    console.log(`${count.toString().padStart(4)}: ${rule}`);
  });

  console.log('\n='.repeat(60));
  console.log(`\nÏ¥ù ${sorted.reduce((sum, [,count]) => sum + count, 0)}Í∞ú ÏóêÎü¨\n`);

  // Show top 5 error types with examples
  console.log('\nüìã ÏÉÅÏúÑ 5Í∞ú ÏóêÎü¨ Ïú†Ìòï ÏòàÏãú:\n');
  sorted.slice(0, 5).forEach(([rule, count]) => {
    console.log(`\n${rule} (${count}Í∞ú):`);
    errorFiles[rule].slice(0, 3).forEach(({ file, line, message }) => {
      console.log(`  - ${file}:${line}: ${message}`);
    });
  });

} catch (error) {
  console.error('Î∂ÑÏÑù Ïã§Ìå®:', error.message);
  process.exit(1);
}

#!/usr/bin/env node
const { execSync } = require('child_process');
const fs = require('fs');

console.log('üîç Ï†ïÍ∑úÏãù Ïù¥Ïäà Î∂ÑÏÑù Ï§ë...\n');

// Run ESLint and save to file
try {
  execSync(
    'npx eslint scripts --format json > eslint-output.json',
    { encoding: 'utf8', shell: true, stdio: 'ignore' }
  );
} catch {
  // ESLint exits with error code when there are errors
}

const data = JSON.parse(fs.readFileSync('eslint-output.json', 'utf8'));

const regexErrors = {
  'concise-regex': [],
  'slow-regex': []
};

// Collect regex-related errors
data.forEach(file => {
  file.messages.forEach(msg => {
    if (msg.severity !== 2) return; // Only errors

    const error = {
      file: file.filePath.split('scripts\\')[1] || file.filePath.split('scripts/')[1],
      line: msg.line,
      column: msg.column,
      message: msg.message,
      ruleId: msg.ruleId
    };

    if (msg.ruleId === 'sonarjs/concise-regex') {
      regexErrors['concise-regex'].push(error);
    } else if (msg.ruleId === 'sonarjs/slow-regex') {
      regexErrors['slow-regex'].push(error);
    }
  });
});

// Display results
console.log('üìã Ï†ïÍ∑úÏãù Ïù¥Ïäà Î∂ÑÏÑù Í≤∞Í≥º:\n');

let totalErrors = 0;

Object.entries(regexErrors).forEach(([category, errors]) => {
  if (errors.length === 0) return;

  console.log(`\nüìå ${category} (${errors.length}Í∞ú):`);
  console.log('‚îÄ'.repeat(70));

  errors.forEach((error, idx) => {
    console.log(`${idx + 1}. ${error.file}:${error.line}:${error.column}`);
    console.log(`   ${error.message}`);
  });

  totalErrors += errors.length;
});

console.log('\n' + '='.repeat(70));
console.log(`üìä Ï¥ù Ï†ïÍ∑úÏãù Ïù¥Ïäà: ${totalErrors}Í∞ú`);
console.log('='.repeat(70));

// Save detailed results
fs.writeFileSync(
  'regex-issues-analysis.json',
  JSON.stringify(regexErrors, null, 2)
);

console.log('\n‚úÖ ÏÉÅÏÑ∏ Í≤∞Í≥ºÍ∞Ä regex-issues-analysis.jsonÏóê Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§.\n');

#!/usr/bin/env node

const { execSync } = require('child_process');
const fs = require('fs');

console.log('üìä ÎÇ®ÏùÄ ÏóêÎü¨ Î∂ÑÏÑù Ï§ë...\n');

// ESLint Ïã§Ìñâ
try {
  execSync('npx eslint scripts --ext .js --format json > scripts-remaining-errors.json', {
    stdio: 'pipe'
  });
} catch {
  // ESLintÍ∞Ä ÏóêÎü¨Î•º Î∞úÍ≤¨ÌïòÎ©¥ exit code 1ÏùÑ Î∞òÌôòÌïòÏßÄÎßå, JSONÏùÄ Ï†ïÏÉÅÏ†ÅÏúºÎ°ú ÏÉùÏÑ±Îê®
}

const data = JSON.parse(fs.readFileSync('scripts-remaining-errors.json', 'utf8'));

// ÏóêÎü¨Î•º ruleÎ≥ÑÎ°ú Î∂ÑÎ•ò
const ruleStats = {};
const ruleExamples = {};

data.forEach(file => {
  const errors = file.messages.filter(m => m.severity === 2);

  errors.forEach(err => {
    const ruleId = err.ruleId || 'unknown';

    if (!ruleStats[ruleId]) {
      ruleStats[ruleId] = 0;
      ruleExamples[ruleId] = [];
    }

    ruleStats[ruleId]++;

    // Ï≤òÏùå 3Í∞ú ÏòàÏ†úÎßå Ï†ÄÏû•
    if (ruleExamples[ruleId].length < 3) {
      ruleExamples[ruleId].push({
        file: file.filePath.split('\\').pop(),
        line: err.line,
        message: err.message
      });
    }
  });
});

// ÏóêÎü¨ ÏàòÎ°ú Ï†ïÎ†¨
const sortedRules = Object.entries(ruleStats).sort((a, b) => b[1] - a[1]);

const totalErrors = sortedRules.reduce((sum, [_, count]) => sum + count, 0);

console.log(`Ï¥ù ${totalErrors}Í∞ú ÏóêÎü¨\n`);
console.log('='.repeat(80));
console.log('RuleÎ≥Ñ ÏóêÎü¨ Ïàò:\n');

// Ïπ¥ÌÖåÍ≥†Î¶¨Î≥ÑÎ°ú Î∂ÑÎ•ò
const categories = {
  security: [],
  complexity: [],
  style: [],
  bestPractice: [],
  other: []
};

sortedRules.forEach(([ruleId, count]) => {
  const entry = { ruleId, count };

  if (ruleId.includes('os-command') || ruleId.includes('hardcoded') || ruleId.includes('sql')) {
    categories.security.push(entry);
  } else if (ruleId.includes('complexity') || ruleId.includes('nested') || ruleId.includes('cognitive')) {
    categories.complexity.push(entry);
  } else if (ruleId.includes('regex') || ruleId.includes('template')) {
    categories.style.push(entry);
  } else if (ruleId.includes('no-') || ruleId.includes('prefer-')) {
    categories.bestPractice.push(entry);
  } else {
    categories.other.push(entry);
  }
});

// Î≥¥Ïïà Ïù¥Ïäà
if (categories.security.length > 0) {
  console.log('\nüîê Î≥¥Ïïà Ïù¥Ïäà:');
  categories.security.forEach(({ ruleId, count }) => {
    console.log(`  ${ruleId.padEnd(50)} ${count}Í∞ú`);
  });
}

// Î≥µÏû°ÎèÑ Ïù¥Ïäà
if (categories.complexity.length > 0) {
  console.log('\nüìê Î≥µÏû°ÎèÑ Ïù¥Ïäà:');
  categories.complexity.forEach(({ ruleId, count }) => {
    console.log(`  ${ruleId.padEnd(50)} ${count}Í∞ú`);
  });
}

// Ïä§ÌÉÄÏùº Ïù¥Ïäà
if (categories.style.length > 0) {
  console.log('\nüé® Ïä§ÌÉÄÏùº Ïù¥Ïäà:');
  categories.style.forEach(({ ruleId, count }) => {
    console.log(`  ${ruleId.padEnd(50)} ${count}Í∞ú`);
  });
}

// Best Practice Ïù¥Ïäà
if (categories.bestPractice.length > 0) {
  console.log('\n‚ú® Best Practice Ïù¥Ïäà:');
  categories.bestPractice.forEach(({ ruleId, count }) => {
    console.log(`  ${ruleId.padEnd(50)} ${count}Í∞ú`);
  });
}

// Í∏∞ÌÉÄ
if (categories.other.length > 0) {
  console.log('\nüìã Í∏∞ÌÉÄ:');
  categories.other.forEach(({ ruleId, count }) => {
    console.log(`  ${ruleId.padEnd(50)} ${count}Í∞ú`);
  });
}

console.log('\n' + '='.repeat(80));

// ÏÉÅÏÑ∏ ÏòàÏ†ú Ï∂úÎ†•
console.log('\n\nüìù Ï£ºÏöî ÏóêÎü¨ ÏòàÏ†ú:\n');

sortedRules.slice(0, 10).forEach(([ruleId, count]) => {
  console.log(`\n${ruleId} (${count}Í∞ú):`);
  ruleExamples[ruleId].forEach(example => {
    console.log(`  ${example.file}:${example.line}`);
    console.log(`    ${example.message}`);
  });
});

// JSONÏúºÎ°úÎèÑ Ï†ÄÏû•
fs.writeFileSync('scripts-error-analysis.json', JSON.stringify({
  totalErrors,
  categories,
  ruleStats,
  ruleExamples
}, null, 2));

console.log('\n\n‚úÖ Î∂ÑÏÑù ÏôÑÎ£å! scripts-error-analysis.jsonÏóê Ï†ÄÏû•Îê®\n');

