async function fetchAllIssues() {
  console.log('üìä Î™®Îì† Ïù¥Ïäà ÏàòÏßë Ï§ë...\n');

  const allIssues = [];
  let page = 1;
  let hasMore = true;
  const pageSize = 500;

  while (hasMore) {
    const endpoint = `/api/issues/search?componentKeys=${PROJECT_KEY}&ps=${pageSize}&p=${page}`;
    const result = await apiCall(endpoint);

    allIssues.push(...result.issues);

    console.log(
      `   ÌéòÏù¥ÏßÄ ${page}: ${result.issues.length}Í∞ú Ïù¥Ïäà ÏàòÏßë (Ï¥ù ${allIssues.length}/${result.total})`
    );

    hasMore = allIssues.length < result.total;
    page++;
  }

  console.log(`\n‚úÖ Ï¥ù ${allIssues.length}Í∞ú Ïù¥Ïäà ÏàòÏßë ÏôÑÎ£å\n`);
  return allIssues;
}

