// Ïù¥Ïäà Î∂ÑÏÑù
function analyzeIssues(issues) {
  console.log('üîç Ïù¥Ïäà Î∂ÑÏÑù Ï§ë...\n');

  const analysis = {
    Ï¥ù_Ïù¥Ïäà_Ïàò: issues.length,
    Ïã¨Í∞ÅÎèÑÎ≥Ñ: {},
    ÌÉÄÏûÖÎ≥Ñ: {},
    ÏÉÅÌÉúÎ≥Ñ: {},
    Î£∞Î≥Ñ: {},
    ÌååÏùºÎ≥Ñ: {},
    ÌÉúÍ∑∏Î≥Ñ: {},
    ÏòÅÌñ•ÎèÑÎ≥Ñ: {
      MAINTAINABILITY: { HIGH: 0, MEDIUM: 0, LOW: 0 },
      RELIABILITY: { HIGH: 0, MEDIUM: 0, LOW: 0 },
      SECURITY: { HIGH: 0, MEDIUM: 0, LOW: 0 },
    },
  };

  issues.forEach((issue) => {
    // Ïã¨Í∞ÅÎèÑÎ≥Ñ
    analysis.Ïã¨Í∞ÅÎèÑÎ≥Ñ[issue.severity] = (analysis.Ïã¨Í∞ÅÎèÑÎ≥Ñ[issue.severity] || 0) + 1;

    // ÌÉÄÏûÖÎ≥Ñ
    analysis.ÌÉÄÏûÖÎ≥Ñ[issue.type] = (analysis.ÌÉÄÏûÖÎ≥Ñ[issue.type] || 0) + 1;

    // ÏÉÅÌÉúÎ≥Ñ
    analysis.ÏÉÅÌÉúÎ≥Ñ[issue.status] = (analysis.ÏÉÅÌÉúÎ≥Ñ[issue.status] || 0) + 1;

    // Î£∞Î≥Ñ (ÏÉÅÏúÑ 20Í∞úÎßå)
    analysis.Î£∞Î≥Ñ[issue.rule] = (analysis.Î£∞Î≥Ñ[issue.rule] || 0) + 1;

    // ÌååÏùºÎ≥Ñ (ÏÉÅÏúÑ 20Í∞úÎßå)
    const fileName = issue.component.split(':').pop();
    analysis.ÌååÏùºÎ≥Ñ[fileName] = (analysis.ÌååÏùºÎ≥Ñ[fileName] || 0) + 1;

    // ÌÉúÍ∑∏Î≥Ñ
    issue.tags?.forEach((tag) => {
      analysis.ÌÉúÍ∑∏Î≥Ñ[tag] = (analysis.ÌÉúÍ∑∏Î≥Ñ[tag] || 0) + 1;
    });

    // ÏòÅÌñ•ÎèÑÎ≥Ñ
    issue.impacts?.forEach((impact) => {
      if (analysis.ÏòÅÌñ•ÎèÑÎ≥Ñ[impact.softwareQuality]) {
        analysis.ÏòÅÌñ•ÎèÑÎ≥Ñ[impact.softwareQuality][impact.severity]++;
      }
    });
  });

  // ÏÉÅÏúÑ Ìï≠Î™©Îßå Ïú†ÏßÄ
  analysis.Î£∞Î≥Ñ = Object.fromEntries(
    Object.entries(analysis.Î£∞Î≥Ñ)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 20)
  );

  analysis.ÌååÏùºÎ≥Ñ = Object.fromEntries(
    Object.entries(analysis.ÌååÏùºÎ≥Ñ)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 20)
  );

  console.log('‚úÖ Ïù¥Ïäà Î∂ÑÏÑù ÏôÑÎ£å\n');
  return analysis;
}

// Ìï´Ïä§Ìåü Î∂ÑÏÑù
function analyzeHotspots(hotspots) {
  if (!hotspots || hotspots.length === 0) {
    return {
      Ï¥ù_Ìï´Ïä§Ìåü_Ïàò: 0,
      ÏÉÅÌÉúÎ≥Ñ: {},
      Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ: {},
      ÌååÏùºÎ≥Ñ: {},
    };
  }

  const analysis = {
    Ï¥ù_Ìï´Ïä§Ìåü_Ïàò: hotspots.length,
    ÏÉÅÌÉúÎ≥Ñ: {},
    Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ: {},
    ÌååÏùºÎ≥Ñ: {},
  };

  hotspots.forEach((hotspot) => {
    // ÏÉÅÌÉúÎ≥Ñ
    analysis.ÏÉÅÌÉúÎ≥Ñ[hotspot.status] = (analysis.ÏÉÅÌÉúÎ≥Ñ[hotspot.status] || 0) + 1;

    // Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ
    analysis.Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ[hotspot.securityCategory] =
      (analysis.Ïπ¥ÌÖåÍ≥†Î¶¨Î≥Ñ[hotspot.securityCategory] || 0) + 1;

    // ÌååÏùºÎ≥Ñ
    const fileName = hotspot.component.split(':').pop();
    analysis.ÌååÏùºÎ≥Ñ[fileName] = (analysis.ÌååÏùºÎ≥Ñ[fileName] || 0) + 1;
  });

  return analysis;
}

// Î©îÌä∏Î¶≠ Î∂ÑÏÑù
function analyzeMetrics(measures) {
  const metrics = {};

  measures.forEach((measure) => {
    metrics[measure.metric] = measure.value;
