    if (varName === 'result' || varName === '_result') {
      varTypes.result++;
    } else if (varName === 'data' || varName === '_data') {
      varTypes.data++;
    } else if (varName === 'response' || varName === '_response') {
      varTypes.response++;
    } else if (varName === 'error' || varName === '_error') {
      varTypes.error++;
    } else if (varName.startsWith('_')) {
