      if (type in counts) counts[type]++;
      else counts.desktop++;
    });

    console.log('\nDevice type counts (all time):');
    console.log('Desktop:', counts.desktop);
    console.log('Mobile:', counts.mobile);
    console.log('Tablet:', counts.tablet);
    console.log('Bot:', counts.bot);
  }
}

