async function checkSecurityAdvisor() {
  try {
    console.log('\nüîê Supabase Security Advisor Ï†êÍ≤Ä ÏãúÏûë')
    console.log('='.repeat(70))

    await client.connect()
    console.log('‚úÖ Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Ïó∞Í≤∞ ÏÑ±Í≥µ!\n')

    // 1. RLSÍ∞Ä ÌôúÏÑ±ÌôîÎêòÏßÄ ÏïäÏùÄ public ÌÖåÏù¥Î∏î ÌôïÏù∏
    console.log('\nüìä 1. RLS (Row Level Security) ÌôúÏÑ±Ìôî ÏÉÅÌÉú ÌôïÏù∏')
    console.log('-'.repeat(70))

    const rlsCheck = await client.query(`
      SELECT
        schemaname,
        tablename,
        rowsecurity as rls_enabled
      FROM pg_tables
      WHERE schemaname = 'public'
      ORDER BY tablename;
    `)

    console.log('\nÌÖåÏù¥Î∏îÎ≥Ñ RLS ÏÉÅÌÉú:')
    console.table(rlsCheck.rows)

    const tablesWithoutRLS = rlsCheck.rows.filter(r => !r.rls_enabled)
    if (tablesWithoutRLS.length > 0) {
      console.log('\n‚ö†Ô∏è  RLSÍ∞Ä ÎπÑÌôúÏÑ±ÌôîÎêú ÌÖåÏù¥Î∏î:')
      tablesWithoutRLS.forEach(t => {
        console.log(`   - ${t.tablename}`)
      })
    } else {
      console.log('\n‚úÖ Î™®Îì† ÌÖåÏù¥Î∏îÏóê RLSÍ∞Ä ÌôúÏÑ±ÌôîÎêòÏñ¥ ÏûàÏäµÎãàÎã§.')
    }

    // 2. RLS Ï†ïÏ±ÖÏù¥ ÏóÜÎäî ÌÖåÏù¥Î∏î ÌôïÏù∏
    console.log('\n\nüìä 2. RLS Ï†ïÏ±Ö Ï°¥Ïû¨ Ïó¨Î∂Ä ÌôïÏù∏')
    console.log('-'.repeat(70))

    const policyCheck = await client.query(`
      SELECT
        t.tablename,
        COUNT(p.policyname) as policy_count
      FROM pg_tables t
      LEFT JOIN pg_policies p ON t.tablename = p.tablename AND t.schemaname = p.schemaname
      WHERE t.schemaname = 'public'
      GROUP BY t.tablename
      ORDER BY COUNT(p.policyname), t.tablename;
    `)

    console.log('\nÌÖåÏù¥Î∏îÎ≥Ñ RLS Ï†ïÏ±Ö Í∞úÏàò:')
    console.table(policyCheck.rows)

    const tablesWithoutPolicies = policyCheck.rows.filter(r => r.policy_count === '0')
    if (tablesWithoutPolicies.length > 0) {
      console.log('\n‚ö†Ô∏è  RLS Ï†ïÏ±ÖÏù¥ ÏóÜÎäî ÌÖåÏù¥Î∏î:')
      tablesWithoutPolicies.forEach(t => {
        console.log(`   - ${t.tablename}`)
      })
    } else {
      console.log('\n‚úÖ Î™®Îì† ÌÖåÏù¥Î∏îÏóê RLS Ï†ïÏ±ÖÏù¥ ÏÑ§Ï†ïÎêòÏñ¥ ÏûàÏäµÎãàÎã§.')
    }

    // 3. Ïô∏ÎûòÌÇ§Í∞Ä ÏûàÏßÄÎßå Ïù∏Îç±Ïä§Í∞Ä ÏóÜÎäî Ïª¨Îüº ÌôïÏù∏
    console.log('\n\nüìä 3. Ïô∏ÎûòÌÇ§ Ïù∏Îç±Ïä§ ÎàÑÎùΩ ÌôïÏù∏')
    console.log('-'.repeat(70))

    const indexCheck = await client.query(`
      SELECT
        tc.table_name,
        kcu.column_name,
        ccu.table_name AS foreign_table_name,
        ccu.column_name AS foreign_column_name
      FROM information_schema.table_constraints AS tc
      JOIN information_schema.key_column_usage AS kcu
        ON tc.constraint_name = kcu.constraint_name
        AND tc.table_schema = kcu.table_schema
      JOIN information_schema.constraint_column_usage AS ccu
        ON ccu.constraint_name = tc.constraint_name
        AND ccu.table_schema = tc.table_schema
      WHERE tc.constraint_type = 'FOREIGN KEY'
        AND tc.table_schema = 'public'
      ORDER BY tc.table_name, kcu.column_name;
    `)

    console.log('\nÏô∏ÎûòÌÇ§ Í¥ÄÍ≥Ñ:')
    console.table(indexCheck.rows)

    // 4. Ïù∏Îç±Ïä§ Î™©Î°ù ÌôïÏù∏
    console.log('\n\nüìä 4. Ïù∏Îç±Ïä§ Î™©Î°ù')
    console.log('-'.repeat(70))

    const indexList = await client.query(`
      SELECT
        schemaname,
        tablename,
        indexname,
        indexdef
      FROM pg_indexes
      WHERE schemaname = 'public'
      ORDER BY tablename, indexname;
    `)

    console.log('\nÏÉùÏÑ±Îêú Ïù∏Îç±Ïä§:')
    console.table(indexList.rows.map(r => ({
      table: r.tablename,
      index: r.indexname,
      definition: r.indexdef.substring(0, 80) + '...'
    })))

    // 5. ÌÖåÏù¥Î∏î ÌÜµÍ≥Ñ (Ìñâ Ïàò, ÌÅ¨Í∏∞)
    console.log('\n\nüìä 5. ÌÖåÏù¥Î∏î ÌÜµÍ≥Ñ')
    console.log('-'.repeat(70))

    const tableStats = await client.query(`
      SELECT
        schemaname,
        tablename,
        pg_size_pretty(pg_total_relation_size(schemaname||'.'||tablename)) AS size,
        n_live_tup as row_count
      FROM pg_stat_user_tables
      WHERE schemaname = 'public'
      ORDER BY pg_total_relation_size(schemaname||'.'||tablename) DESC;
    `)

    console.log('\nÌÖåÏù¥Î∏îÎ≥Ñ ÌÅ¨Í∏∞ Î∞è Ìñâ Ïàò:')
    console.table(tableStats.rows)

    // 6. Î≥¥Ïïà Í∂åÍ≥†ÏÇ¨Ìï≠ ÏöîÏïΩ
    console.log('\n\nüîê Î≥¥Ïïà Í∂åÍ≥†ÏÇ¨Ìï≠ ÏöîÏïΩ')
    console.log('='.repeat(70))

    let issueCount = 0

    if (tablesWithoutRLS.length > 0) {
      console.log(`\n‚ö†Ô∏è  [ÎÜíÏùå] RLS ÎπÑÌôúÏÑ±ÌôîÎêú ÌÖåÏù¥Î∏î: ${tablesWithoutRLS.length}Í∞ú`)
      issueCount++
    }

    if (tablesWithoutPolicies.length > 0) {
      console.log(`\n‚ö†Ô∏è  [ÎÜíÏùå] RLS Ï†ïÏ±Ö ÏóÜÎäî ÌÖåÏù¥Î∏î: ${tablesWithoutPolicies.length}Í∞ú`)
      issueCount++
    }

    if (issueCount === 0) {
      console.log('\n‚úÖ Ïã¨Í∞ÅÌïú Î≥¥Ïïà Î¨∏Ï†úÍ∞Ä Î∞úÍ≤¨ÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§!')
    } else {
      console.log(`\n‚ö†Ô∏è  Ï¥ù ${issueCount}Í∞úÏùò Î≥¥Ïïà Î¨∏Ï†úÍ∞Ä Î∞úÍ≤¨ÎêòÏóàÏäµÎãàÎã§.`)
    }

    console.log('\n' + '='.repeat(70))
    console.log('‚úÖ Security Advisor Ï†êÍ≤Ä ÏôÑÎ£å!')

  } catch (error) {
    console.error('\n‚ùå Ïò§Î•ò Î∞úÏÉù:', error.message)
    if (error.detail) console.error('ÏÉÅÏÑ∏:', error.detail)
    process.exit(1)
  } finally {
    await client.end()
  }
}

