    console.log('\nüìä ÌÖåÏù¥Î∏î Íµ¨Ï°∞ ÌôïÏù∏')
    console.log('='.repeat(70))

    await client.connect()
    console.log('‚úÖ Îç∞Ïù¥ÌÑ∞Î≤†Ïù¥Ïä§ Ïó∞Í≤∞ ÏÑ±Í≥µ!\n')

    const tables = ['disputes', 'seller_earnings', 'settlements']

    for (const tableName of tables) {
      console.log(`\nüìã ${tableName.toUpperCase()} ÌÖåÏù¥Î∏î Íµ¨Ï°∞`)
      console.log('-'.repeat(70))

      // Ïª¨Îüº Ï†ïÎ≥¥ Ï°∞Ìöå
      const columns = await client.query(`
        SELECT
          column_name,
          data_type,
          is_nullable,
          column_default,
          character_maximum_length
        FROM information_schema.columns
        WHERE table_schema = 'public'
          AND table_name = $1
        ORDER BY ordinal_position;
      `, [tableName])

      console.table(columns.rows)

      // Ïô∏ÎûòÌÇ§ Ï†ïÎ≥¥ Ï°∞Ìöå
      const foreignKeys = await client.query(`
        SELECT
          kcu.column_name,
          ccu.table_name AS foreign_table_name,
          ccu.column_name AS foreign_column_name
        FROM information_schema.table_constraints AS tc
        JOIN information_schema.key_column_usage AS kcu
          ON tc.constraint_name = kcu.constraint_name
          AND tc.table_schema = kcu.table_schema
        JOIN information_schema.constraint_column_usage AS ccu
          ON ccu.constraint_name = tc.constraint_name
          AND ccu.table_schema = tc.table_schema
        WHERE tc.constraint_type = 'FOREIGN KEY'
          AND tc.table_schema = 'public'
          AND tc.table_name = $1
        ORDER BY kcu.column_name;
      `, [tableName])

      if (foreignKeys.rows.length > 0) {
        console.log('\nüîó Ïô∏ÎûòÌÇ§ Í¥ÄÍ≥Ñ:')
