async function exportFunctions() {
  console.log('üìã Exporting database functions...');

  try {
    const functions = await executeQuery(`
      SELECT
        n.nspname as schema,
        p.proname as name,
        pg_get_function_arguments(p.oid) as arguments,
        pg_get_functiondef(p.oid) as definition,
        CASE p.provolatile
          WHEN 'i' THEN 'IMMUTABLE'
          WHEN 's' THEN 'STABLE'
          WHEN 'v' THEN 'VOLATILE'
        END as volatility,
        CASE p.prosecdef
          WHEN true THEN 'SECURITY DEFINER'
          ELSE 'SECURITY INVOKER'
        END as security
      FROM pg_proc p
      JOIN pg_namespace n ON p.pronamespace = n.oid
      WHERE n.nspname NOT IN ('pg_catalog', 'information_schema')
      AND p.prokind = 'f'
      ORDER BY n.nspname, p.proname
    `);

    const outputPath = path.join(OUTPUT_DIR, 'database-functions.json');
    fs.writeFileSync(outputPath, JSON.stringify(functions, null, 2));

    // SQL ÌòïÌÉúÎ°úÎèÑ Ï†ÄÏû•
    const sqlPath = path.join(OUTPUT_DIR, 'database-functions.sql');
    const sqlContent = functions.map(f => f.definition).join('\n\n');
    fs.writeFileSync(sqlPath, sqlContent);

    console.log(`‚úÖ Exported ${functions.length} database functions\n`);
  } catch (error) {
    console.error(`‚ùå Error: ${error.message}\n`);
  }
}

