console.log('üìä @typescript-eslint/no-unused-vars ÏóêÎü¨ Ï∂îÏ∂ú Ï§ë...\n');

try {
  const output = execSync('npx eslint scripts --ext .js --format json', {
    encoding: 'utf8',
    stdio: ['pipe', 'pipe', 'pipe']
  });

  const data = JSON.parse(output);

  // @typescript-eslint/no-unused-vars ÏóêÎü¨Îßå ÌïÑÌÑ∞ÎßÅ
  const unusedVarsErrors = [];

  data.forEach(file => {
    const errors = file.messages.filter(m =>
      m.ruleId === '@typescript-eslint/no-unused-vars' && m.severity === 2
    );

    if (errors.length > 0) {
      unusedVarsErrors.push({
        filePath: file.filePath,
        errorCount: errors.length,
        errors: errors.map(e => ({
          line: e.line,
          column: e.column,
          message: e.message,
          variable: e.message.match(/'([^']+)'/)?.[1]
        }))
      });
    }
  });

  // ÌååÏùºÎ≥ÑÎ°ú Ï†ïÎ¶¨
  console.log(`Ï¥ù ${unusedVarsErrors.reduce((sum, f) => sum + f.errorCount, 0)}Í∞ú ÏóêÎü¨ Î∞úÍ≤¨\n`);
  console.log('ÌååÏùºÎ≥Ñ ÏóêÎü¨ Ïàò:');

  unusedVarsErrors.forEach(file => {
    const fileName = file.filePath.split('\\').pop();
    console.log(`  ${fileName}: ${file.errorCount}Í∞ú`);
  });

  // JSON ÌååÏùºÎ°ú Ï†ÄÏû•
  fs.writeFileSync('scripts-unused-vars.json', JSON.stringify(unusedVarsErrors, null, 2));

  console.log('\n‚úÖ scripts-unused-vars.json ÌååÏùºÏóê Ï†ÄÏû•Îê®\n');

  // ÏÉòÌîå Ï∂úÎ†•
  console.log('ÏÉòÌîå ÏóêÎü¨Îì§:');
