const https = require('https');
const fs = require('fs');
const path = require('path');

const NEW_PROJECT_ID = 'abroivxthindezdtdzmj';
const NEW_ACCESS_TOKEN = 'sbp_f40b15f794e727f0aa9161de38c497174fcac2ee';
const JSON_DIR = path.join(__dirname, '..', 'database-export');

function executeQuery(query) {
  return new Promise((resolve, reject) => {
    const data = JSON.stringify({ query });

    const options = {
      hostname: 'api.supabase.com',
      path: `/v1/projects/${NEW_PROJECT_ID}/database/query`,
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${NEW_ACCESS_TOKEN}`,
        'Content-Type': 'application/json',
        'Content-Length': Buffer.byteLength(data)
      }
    };

    const req = https.request(options, (res) => {
      let body = '';

      res.on('data', (chunk) => {
        body += chunk;
      });

      res.on('end', () => {
        if (res.statusCode === 200 || res.statusCode === 201) {
          try {
            resolve(JSON.parse(body));
          } catch (error) {
            console.error('ÏóêÎü¨ Î∞úÏÉù:', error);
            resolve({ success: true });
          }
        } else {
          reject(new Error(`HTTP ${res.statusCode}: ${body}`));
        }
      });
    });

    req.on('error', reject);
    req.write(data);
    req.end();
  });
}

async function importWithRetry(tableName, maxRetries = 3) {
  const jsonPath = path.join(JSON_DIR, `${tableName}.json`);

  if (!fs.existsSync(jsonPath)) {
    console.log(`   ‚ö†Ô∏è  ${tableName}.json not found\n`);
    return { success: 0, failed: 0 };
  }

  try {
    console.log(`\nüì¶ Re-importing ${tableName}...`);

    const data = JSON.parse(fs.readFileSync(jsonPath, 'utf8'));

    if (!data || data.length === 0) {
      console.log(`   ‚ö†Ô∏è  No data\n`);
      return { success: 0, failed: 0 };
    }

    const columns = Object.keys(data[0]);
    let successCount = 0;
    let failCount = 0;

    // Temporarily disable triggers to avoid FK issues
    try {
      await executeQuery(`ALTER TABLE "${tableName}" DISABLE TRIGGER ALL`);
    } catch (error) {
      console.error('ÏóêÎü¨ Î∞úÏÉù:', error);
      // Ignore
    }

    for (let i = 0; i < data.length; i++) {
      const row = data[i];
      let success = false;

      for (let attempt = 0; attempt < maxRetries && !success; attempt++) {
        try {
          const values = columns.map(col => {
            const value = row[col];

            if (value === null || value === undefined) {
              return 'NULL';
            }

            if (typeof value === 'string') {
              const escaped = value.replace(/'/g, "''").replace(/\\/g, '\\\\');
              return `'${escaped}'`;
            }

            if (typeof value === 'boolean') {
              return value ? 'true' : 'false';
            }

            if (typeof value === 'object') {
              const json = JSON.stringify(value).replace(/'/g, "''");
              return `'${json}'::jsonb`;
            }

            return value;
          }).join(', ');

          const insertQuery = `
            INSERT INTO "${tableName}" (${columns.map(c => `"${c}"`).join(', ')})
            VALUES (${values})
            ON CONFLICT DO NOTHING
          `;

          await executeQuery(insertQuery);
          successCount++;
          success = true;

        } catch {
          if (attempt === maxRetries - 1) {
            failCount++;
          }
          // Wait a bit before retry
          await new Promise(resolve => setTimeout(resolve, 100 * (attempt + 1)));
        }
      }

      if ((i + 1) % 10 === 0 || i === data.length - 1) {
        process.stdout.write(`\r   Progress: ${i + 1}/${data.length} (‚úÖ ${successCount}, ‚ùå ${failCount})`);
      }
    }

    // Re-enable triggers
    try {
      await executeQuery(`ALTER TABLE "${tableName}" ENABLE TRIGGER ALL`);
    } catch (error) {
      console.error('ÏóêÎü¨ Î∞úÏÉù:', error);
      // Ignore
    }

    console.log(`\n   ‚úÖ Completed: ${successCount} success, ${failCount} failed\n`);

    return { success: successCount, failed: failCount };

  } catch (error) {
    console.log(`\n   ‚ùå Error: ${error.message}\n`);
    return { success: 0, failed: data?.length || 0 };
  }
}

