  try {
    console.log(`üì¶ Importing ${tableName}...`);

    const data = JSON.parse(fs.readFileSync(jsonPath, 'utf8'));

    if (!data || data.length === 0) {
      console.log(`   ‚ö†Ô∏è  No data, skipping\n`);
      return;
    }

    const columns = Object.keys(data[0]);
    let successCount = 0;
    let errorCount = 0;

