async function uploadTable(tableName, data) {
  if (!data || data.length === 0) {
    console.log(`   ‚ö†Ô∏è  No data to upload\n`);
    return { success: 0, failed: 0 };
  }

  console.log(`üì§ Uploading ${data.length} rows to new DB...`);

  // Get column types first
  const columnTypes = await getColumnTypes(tableName);

  const columns = Object.keys(data[0]);
  let successCount = 0;
  let failCount = 0;

  for (let i = 0; i < data.length; i++) {
    const row = data[i];

    try {
      const values = columns.map(col => {
        const value = row[col];
        const colType = columnTypes[col];

        if (value === null || value === undefined) {
          return 'NULL';
        }

        // Handle array types (text[], varchar[], etc.)
        if (colType && colType.udt_name && colType.udt_name.startsWith('_')) {
          if (Array.isArray(value)) {
            if (value.length === 0) {
              return 'ARRAY[]::text[]';
            }
            const arrayValues = value.map(v => {
              const escaped = String(v).replace(/'/g, "''").replace(/\\/g, '\\\\');
              return `'${escaped}'`;
            }).join(', ');
            return `ARRAY[${arrayValues}]`;
          }
          // If it's stored as JSON string but needs to be array
          if (typeof value === 'string' && value.startsWith('[')) {
            try {
              const parsed = JSON.parse(value);
              if (Array.isArray(parsed)) {
                if (parsed.length === 0) {
                  return 'ARRAY[]::text[]';
                }
                const arrayValues = parsed.map(v => {
                  const escaped = String(v).replace(/'/g, "''").replace(/\\/g, '\\\\');
                  return `'${escaped}'`;
                }).join(', ');
                return `ARRAY[${arrayValues}]`;
              }
            } catch (error) {
              console.error('ÏóêÎü¨ Î∞úÏÉù:', error);
              // Fall through
            }
          }
        }

        if (typeof value === 'string') {
          const escaped = value.replace(/'/g, "''").replace(/\\/g, '\\\\');
          return `'${escaped}'`;
        }

        if (typeof value === 'boolean') {
          return value ? 'true' : 'false';
        }

        if (typeof value === 'object') {
          const json = JSON.stringify(value).replace(/'/g, "''");
          return `'${json}'::jsonb`;
        }

        return value;
      }).join(', ');

      const insertQuery = `
        INSERT INTO "${tableName}" (${columns.map(c => `"${c}"`).join(', ')})
        VALUES (${values})
        ON CONFLICT DO NOTHING
      `;

      await executeQuery(NEW_PROJECT_ID, NEW_ACCESS_TOKEN, insertQuery);
      successCount++;

    } catch (error) {
      console.error('ÏóêÎü¨ Î∞úÏÉù:', error);
      failCount++;
      console.log(`\n   ‚ùå Row ${i + 1} failed: ${error.message}`);
      console.log(`      Data: ${JSON.stringify(row).substring(0, 200)}...\n`);
    }

    if ((i + 1) % 10 === 0 || i === data.length - 1) {
      process.stdout.write(`\r   Progress: ${i + 1}/${data.length} (‚úÖ ${successCount}, ‚ùå ${failCount})`);
    }
  }

  console.log(`\n   ‚úÖ Completed: ${successCount} success, ${failCount} failed\n`);

  return { success: successCount, failed: failCount };
}

