const { execSync } = require('child_process');
const https = require('https');
const http = require('http');

// ÏÉâÏÉÅ ÏΩîÎìú
const colors = {
  reset: '\x1b[0m',
  red: '\x1b[31m',
  green: '\x1b[32m',
  yellow: '\x1b[33m',
  blue: '\x1b[34m',
  cyan: '\x1b[36m',
};

function log(message, color = 'reset') {
  console.log(`${colors[color]}${message}${colors.reset}`);
}

function logStep(step, message) {
  console.log(`\n${colors.cyan}[${step}]${colors.reset} ${message}`);
}

function logSuccess(message) {
  console.log(`${colors.green}‚úì${colors.reset} ${message}`);
}

function logError(message) {
  console.log(`${colors.red}‚úó${colors.reset} ${message}`);
}

function logWarning(message) {
  console.log(`${colors.yellow}‚ö†${colors.reset} ${message}`);
}

// Î™ÖÎ†πÏñ¥ Ïã§Ìñâ Ìï®Ïàò
function runCommand(command, description) {
  logStep('Ïã§Ìñâ', description);
  try {
    execSync(command, { stdio: 'inherit', encoding: 'utf8' });
    logSuccess(`${description} ÏôÑÎ£å`);
    return true;
  } catch {
    logError(`${description} Ïã§Ìå®`);
    return false;
  }
}

