async function loadLeafCategories() {
  console.log('\nüìÇ ÏµúÌïòÏúÑ Ïπ¥ÌÖåÍ≥†Î¶¨ Î°úÎî© Ï§ë...\n');

  const { data: allCategories, error: allError } = await supabase
    .from('categories')
    .select('id, name, parent_id, slug')
    .order('id');

  if (allError) throw allError;

  // ÏµúÌïòÏúÑ Ïπ¥ÌÖåÍ≥†Î¶¨ (ÏûêÏãùÏù¥ ÏóÜÎäî Ïπ¥ÌÖåÍ≥†Î¶¨)
  const childIds = new Set(allCategories.map(c => c.parent_id).filter(Boolean));
  const leafCategories = allCategories.filter(c => !childIds.has(c.id));

  console.log(`‚úì ÏµúÌïòÏúÑ Ïπ¥ÌÖåÍ≥†Î¶¨ ${leafCategories.length}Í∞ú Î°úÎìúÎê®\n`);
  stats.totalCategories = leafCategories.length;

  return leafCategories;
}

// 2. Auth ÏÇ¨Ïö©Ïûê ÏÉùÏÑ±
async function createAuthUser(email, password, name) {
  try {
    const response = await fetch(AUTH_ADMIN_URL, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${SERVICE_ROLE_KEY}`,
        'Content-Type': 'application/json',
        'apikey': SERVICE_ROLE_KEY
      },
      body: JSON.stringify({
        email: email,
        password: password,
        email_confirm: true,
        user_metadata: {
          name: name
        }
      })
    });

    const data = await response.json();

    if (!response.ok) {
      // Ïù¥ÎØ∏ Ï°¥Ïû¨ÌïòÎäî ÏÇ¨Ïö©ÏûêÏù∏ Í≤ΩÏö∞
      if (data.msg && data.msg.includes('already been registered')) {
        return { exists: true, email };
      }
      throw new Error(data.msg || JSON.stringify(data));
    }

    return { user_id: data.id, email: data.email };
  } catch (error) {
    console.error('Auth user creation error:', error.message);
    throw error;
  }
}

