    // 2. Î™®Îì† Íµ¨ÎèÖ Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
    const { data: allSubscriptions, error: subsError } = await supabase
      .from('advertising_subscriptions')
