        .from('advertising_subscriptions')
        .update({
          status: 'cancelled',
          cancelled_at: new Date().toISOString(),
          updated_at: new Date().toISOString()
        })
        .in('id', cancelledIds);

      if (error) {
        console.error('cancelled ÏóÖÎç∞Ïù¥Ìä∏ Ïã§Ìå®:', error);
