    if (pendingIds.length > 0) {
      const { error } = await supabase
        .from('advertising_subscriptions')
        .update({
          status: 'pending_payment',
          updated_at: new Date().toISOString()
        })
        .in('id', pendingIds);

      if (error) {
        console.error('pending_payment ÏóÖÎç∞Ïù¥Ìä∏ Ïã§Ìå®:', error);
      } else {
        totalUpdated += pendingIds.length;
        console.log(`‚úì ${pendingIds.length}Í±¥ÏùÑ pending_paymentÎ°ú ÏóÖÎç∞Ïù¥Ìä∏`);
      }
    }

    console.log(`\n=== ÏôÑÎ£å: Ï¥ù ${totalUpdated}Í±¥ ÎèôÍ∏∞ÌôîÎê® ===\n`);

  } catch (error) {
    console.error('ÎèôÍ∏∞Ìôî Ïã§Ìå®:', error);
  }
}

