function runCommand(command, args, inputs) {
  return new Promise((resolve, reject) => {
    console.log(`\nÏã§Ìñâ: ${command} ${args.join(' ')}`);

    const proc = spawn(command, args, {
      stdio: ['pipe', 'pipe', 'pipe'],
      shell: true
    });

    let output = '';
    let inputIndex = 0;

    proc.stdout.on('data', (data) => {
      const text = data.toString();
      output += text;
      process.stdout.write(text);

      // ÏûÖÎ†• ÌîÑÎ°¨ÌîÑÌä∏ Í∞êÏßÄ
      if (inputs && inputIndex < inputs.length) {
        if (text.includes('?') || text.includes(':') || text.includes('Enter')) {
          setTimeout(() => {
            console.log(`  ‚Üí ÏûÖÎ†•: ${inputs[inputIndex]}`);
            proc.stdin.write(inputs[inputIndex] + '\n');
            inputIndex++;
          }, 500);
        }
      }
    });

    proc.stderr.on('data', (data) => {
      process.stderr.write(data);
    });

    proc.on('close', () => {
      // Always resolve - some vercel commands return non-zero exit codes
      // codeÍ∞Ä 0Ïù¥ ÏïÑÎãàÏñ¥ÎèÑ ÏÑ±Í≥µÏúºÎ°ú Ï≤òÎ¶¨ (ÏùºÎ∂Ä vercel Î™ÖÎ†πÏñ¥Îäî ÎπÑÏ†ïÏÉÅ Ï¢ÖÎ£å ÏΩîÎìú Î∞òÌôò)
      resolve(output);
    });

    proc.on('error', (err) => {
      reject(err);
    });
  });
}

