async function verifyTable(tableName) {
  try {
    const oldResult = await executeQuery(OLD_PROJECT_ID, OLD_ACCESS_TOKEN,
      `SELECT COUNT(*) as count FROM "${tableName}"`
    );
    const newResult = await executeQuery(NEW_PROJECT_ID, NEW_ACCESS_TOKEN,
      `SELECT COUNT(*) as count FROM "${tableName}"`
    );

    const oldCount = parseInt(oldResult[0].count);
    const newCount = parseInt(newResult[0].count);
    const match = oldCount === newCount;

    let status = '‚ö†Ô∏è';
    if (match) {
      status = '‚úÖ';
    } else if (oldCount === 0) {
      status = '‚ö™';
    }

    const diff = newCount - oldCount;
    let diffStr = '';
    if (diff > 0) {
      diffStr = `+${diff}`;
    } else if (diff < 0) {
      diffStr = `${diff}`;
    }

    console.log(`${status} ${tableName.padEnd(25)} | ÏõêÎ≥∏: ${String(oldCount).padStart(5)} | ÏÉà: ${String(newCount).padStart(5)} ${diffStr}`);

    return { tableName, oldCount, newCount, match };

  } catch (error) {
    console.error('ÏóêÎü¨ Î∞úÏÉù:', error);
    console.log(`‚ùå ${tableName.padEnd(25)} | Error: ${error.message}`);
    return { tableName, oldCount: 0, newCount: 0, match: false, error: true };
  }
}

async function verifyDatabaseObjects() {
