    console.log('-'.repeat(70))

    let totalPolicies = 0
    let optimizedTables = 0
    let issuesFound = 0

    for (const tableName of tables) {
      await client.query(`
        SELECT
          schemaname,
          tablename,
          policyname,
          cmd,
          qual,
          with_check
        FROM pg_policies
        WHERE schemaname = 'public'
          AND tablename = $1
