  errors.forEach(err => {
    const ruleId = err.ruleId || 'unknown';

    if (!ruleStats[ruleId]) {
      ruleStats[ruleId] = 0;
      ruleExamples[ruleId] = [];
    }

    ruleStats[ruleId]++;

    // ì²˜ìŒ 3ê°œ ì˜ˆì œë§Œ ì €ì¥
    if (ruleExamples[ruleId].length < 3) {
      ruleExamples[ruleId].push({
        file: file.filePath.split('\\').pop(),
        line: err.line,
        message: err.message
      });
    }
  });
});

// ì—ëŸ¬ ìˆ˜ë¡œ ì •ë ¬
