    // Execute SQL directly using Supabase SQL editor API
    await fetch(`${supabaseUrl}/rest/v1/rpc/exec`, {
      method: 'POST',
      headers: {
        'apikey': supabaseServiceKey,
        'Authorization': `Bearer ${supabaseServiceKey}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ query: sql })
    });

    // Alternative: Use psql-style execution
    const { error } = await supabase.rpc('exec_sql', { sql });

    if (error) {
      console.error('âŒ Migration failed:', error.message);
      console.error('Details:', error);
      return false;
    }

    console.log('âœ… Migration applied successfully!');
    return true;

  } catch (err) {
    console.error('âŒ Error executing migration:', err.message);

    // Try alternative approach: split and execute statements
    console.log('\nğŸ”„ Trying alternative approach: executing statements individually...');

    const statements = sql
      .split(';')
      .map(s => s.trim())
      .filter(s => s.length > 0 && !s.startsWith('--'));

    let successCount = 0;
    let errorCount = 0;

    for (let i = 0; i < statements.length; i++) {
      const stmt = statements[i] + ';';

      // Skip comment-only lines
      if (stmt.replace(/\s+/g, '') === ';') continue;

      console.log(`\nExecuting statement ${i + 1}/${statements.length}...`);

      try {
        const { error: stmtError } = await supabase.rpc('exec_sql', { sql: stmt });

        if (stmtError) {
          console.error(`  âŒ Failed:`, stmtError.message);
          errorCount++;
        } else {
          console.log(`  âœ… Success`);
          successCount++;
        }
      } catch (stmtErr) {
        console.error(`  âŒ Error:`, stmtErr.message);
        errorCount++;
      }
    }

    console.log(`\nğŸ“Š Results: ${successCount} succeeded, ${errorCount} failed`);
    return errorCount === 0;
  }
}

