async function createBucket(bucket) {
  console.log(`ğŸ“¦ Creating bucket: ${bucket.name}`);

  try {
    const mimeTypes = bucket.allowed_mime_types
      ? `ARRAY[${bucket.allowed_mime_types.map(m => `'${m}'`).join(', ')}]::text[]`
      : 'NULL';

    const query = `
      INSERT INTO storage.buckets (id, name, public, file_size_limit, allowed_mime_types)
      VALUES (
        '${bucket.id}',
        '${bucket.name}',
        ${bucket.public},
        ${bucket.file_size_limit || 'NULL'},
        ${mimeTypes}
      )
      ON CONFLICT (id) DO UPDATE SET
        name = EXCLUDED.name,
        public = EXCLUDED.public,
        file_size_limit = EXCLUDED.file_size_limit,
        allowed_mime_types = EXCLUDED.allowed_mime_types
    `;

    await executeQuery(query);
    console.log(`   âœ… Created: ${bucket.name} (${bucket.public ? 'public' : 'private'})\n`);

  } catch (error) {
    console.log(`   âŒ Error: ${error.message}\n`);
  }
}

async function createBucketPolicies() {
  console.log('ğŸ” Creating storage policies...\n');

  // Public buckets - allow public read
  const publicBuckets = ['profiles', 'services', 'portfolio', 'uploads', 'food-stores'];

  for (const bucketName of publicBuckets) {
    try {
      const policyName = `public_read_${bucketName}`;

      await executeQuery(`
        DROP POLICY IF EXISTS "${policyName}" ON storage.objects;
      `);

      await executeQuery(`
        CREATE POLICY "${policyName}"
        ON storage.objects FOR SELECT
        TO public
        USING (bucket_id = '${bucketName}');
      `);

      console.log(`   âœ… Policy: ${policyName}`);
    } catch (error) {
      console.error('ì—ëŸ¬ ë°œìƒ:', error);
