    const columns = Object.keys(data[0]);

    // Create CSV content
    const rows = data.map(row => {
      return columns.map(col => escapeCSV(row[col])).join('\t');
    });

    const csvContent = rows.join('\n');

    // Write to file
    const outputPath = path.join(OUTPUT_DIR, `${tableName}.csv`);
