async function importTable(tableName) {
  const filePath = path.join(DATA_DIR, `${tableName}.json`);

  if (!fs.existsSync(filePath)) {
    console.log(`   âš ï¸  No data file found, skipping\n`);
    return;
  }

  try {
    const data = JSON.parse(fs.readFileSync(filePath, 'utf8'));

    if (!data || data.length === 0) {
      console.log(`   âš ï¸  Empty data, skipping\n`);
      return;
    }

    // INSERT ë¬¸ ìƒì„±
    const columns = Object.keys(data[0]);
    const values = data.map(row => {
      const rowValues = columns.map(col => {
        const value = row[col];
        if (value === null) return 'NULL';
        if (typeof value === 'string') return `'${value.replace(/'/g, "''")}'`;
        if (typeof value === 'boolean') return value ? 'true' : 'false';
        if (typeof value === 'object') return `'${JSON.stringify(value).replace(/'/g, "''")}'`;
        return value;
      });
      return `(${rowValues.join(', ')})`;
    });

    // ë°°ì¹˜ ì²˜ë¦¬ (í•œ ë²ˆì— 100ê°œì”©)
    const batchSize = 100;
    let imported = 0;

    for (let i = 0; i < values.length; i += batchSize) {
      const batch = values.slice(i, i + batchSize);
      const insertQuery = `
        INSERT INTO "${tableName}" (${columns.map(c => `"${c}"`).join(', ')})
        VALUES ${batch.join(', ')}
      `;

      await executeQuery(insertQuery);
      imported += batch.length;

      process.stdout.write(`\r   ğŸ“¦ Imported ${imported}/${data.length} rows...`);
    }

    console.log(`\n   âœ… Completed\n`);
  } catch (error) {
    console.log(`\n   âŒ Error: ${error.message}\n`);
  }
}

